import app from './firebase';

import { useState } from 'react';

import { getAuth, createUserWithEmailAndPassword, onAuthStateChanged } from "firebase/auth";


function Contacts  ()  {

    const auth = getAuth(app);

    const [email, setEmail] = useState("");
    const [password, setPass] = useState("")


  const singup =() =>{
    createUserWithEmailAndPassword(auth, email, password)
  .then((userCredential) => {
    // Signed in 
    const user = userCredential.user;
    console.log(user)
    alert("bien creee")
    // ...
  })
  .catch((error) => {
    const errorCode = error.code;
    alert(errorCode)
    //const errorMessage = error.message;
    // ..
  });

  }



    return (
        <div className='contacts'>
            {/* <Navigation/> */}

        <div className='mydiv'>

          <div className="main">
     <div className="sub-main">
       <div>
         <div className="imgs">
           <div className="container-image">


           </div>


         </div>
         <div>
           <h1>SignUp</h1>
           <div>
             <img src={email} alt="email" className="email"/>
             <input type={"email"}  placeholder="user name" className="name" onChange={(e) => setEmail(e.target.value)}/>
           </div>
           <div className="second-input">

             <input type={"password"}  placeholder="user name" className="name" onChange={(e) => setPass(e.target.value)} />
           </div>
          <div className="login-button">
          <button onClick={singup}>SignUp</button>
          </div>

            <p className="link">
              <a href="K">Forgot password ?</a> Or<a href="K">Sign Up</a>
            </p>

 
         </div>
       </div>


     </div>
    </div>
    </div> 

        </div>
    );
};

export default Contacts;
